import time 
#Iterative Cumulative Method
def sumOfN2(n):
    start = time.time()#开始计时
    theSum = 0
    for i in range(1,n+1):
        theSum += i
    end = time.time()
    return theSum, end - start

for i in range(5):
    print("Sum is %d required %10.7f seconds" %sumOfN2(100000))

import time
# Iterationless cumulative method
def sumOfN3(n):
    start = time.time()
    theSum = (n*(1+n))/2
    end = time.time()
    return theSum,end - start

for i in range(5):
    print("Sum is %d required %10.7f seconds" %sumOfN3(100000))

def anagramSolution3(s1,s2):
    c1 = [0]*26
    c2 = [0]*26
    for i in range(len(s1)):
        pos = ord(s1[i]) - ord('a')#ord function returns the unicode encoding of the character, mapping a to z by subtracting the encoding of a; of course a dictionary can also be used
    for i in range(len(s2)):
        pos = ord(s2[i]) - ord('a')
        c2[pos] += 1
    j = 0
    stillOK = True
    while j < 26 and stillOK:
        if c1[j] == c2[j]:
            j += 1
        else:
            stillOK = False
    return stillOK
print(anagramSolution3('abc','dca'))

