def anagramSolution_dict(s1, s2):
    # Create empty dictionaries
    dic1 = {}
    dic2 = {}

    for i in s1:
        dic1[i] = dic1.get(i,0) + 1# set default to 0

    for i in s2:
        dic2[i] = dic2.get(i,0) + 1

    return dic1 == dic2

print(anagramSolution_dict('abc','dca'))
