# Model code
def pow(x, n):
    if n == 0: 
        return 1 # Base case: any number to the 0th power of 1
    elif n == 1: 
        return x # Base case: any number to the 1st power of itself
    elif n % 2 == 0:
        return pow(x*x, n//2) # Even numbers: convert to (x²)^(n/2)
    else:
        return pow(x*x, n//2) * x # Odd numbers: convert to x * (x²)^(n//2)
